mapper.delete:
		delete  from user where username in (

            #{username}

        )

mapper.select:
select * from user u
	left join department d on u.department_id = d.departmentID
	left join position p on u.position_id = p.positionID


	    p.positionName = #{positionName}


		and u.username = #{username}


		and u.idCardNo = #{idCardNo}


		and u.name = #{name}


		and d.departmentName = #{departmentName}


order by u.username

mapper.update:
update user
set password = #{password}

	,phone = #{phone}


	,groupId = #{groupId}


	,name = #{name}


	,sex = #{sex}


	,email = #{email}

	,position_id = #{position_id}


	,education = #{education}


	,idCardNo = #{idCardNo}


	,department_id = #{department_id}


	,address = #{address}


	,createdDate = #{createdDate}

where username = #{username}

update user
set face_id = #{faceid}
where user_id = #{user_id}
